BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS `Words` (
	`ID`	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,
	`Name`	TEXT NOT NULL,
	`Mean`	TEXT,
	`Pic`	BLOB,
	`PicClick`	BLOB,
	`Ex`	TEXT,
	`TopicId`	INTEGER
);
CREATE TABLE IF NOT EXISTS `TypeLang` (
	`ID`	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,
	`Name`	TEXT NOT NULL
);
CREATE TABLE IF NOT EXISTS `Topic` (
	`ID`	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,
	`Name`	TEXT NOT NULL,
	`TypeId`	INTEGER,
	`Icon2`	BLOB,
	`Icon1`	BLOB
);
COMMIT;
